import React, { useEffect, useState } from 'react';
import './note.css';
const Notes = () => {
    const [items, setItems] = useState([]);
    useEffect(() => {
        localStorage.setItem('items',JSON.stringify(items));
    }, [items]);
    const [detail, setDetail] = useState({
        title:"",
        desp:""
    });
    const inputEvent = (event) => {
        const value = event.target.value;
        const name = event.target.name;
        setDetail((prev) => {
            return {
                ...prev,
                [name] : value
            }
        });
    }
    const listOfNotes = () =>{
        setItems((old) => {
            return [...old,detail];
        })
        setDetail("");
    }
    return (
        <>
            <div className="container">
                <div className="innerCont">
                    <label className="formLabel" >Title</label>
                    <input type="text" name='title' className="formControl" id="title" onChange={inputEvent} value={detail.title}/>
                </div>
                <div className="innerCont" >
                    <label className="formLabel" >Description</label>
                    <textarea className="formControl" name='desp' id="description" rows={3} onChange={inputEvent} value={detail.desp}/>
                </div>
                <div className="innerCont">
                    <button type="button" id="addNote" onClick={listOfNotes}>Add Note</button>
                </div>
                <div className="innerCont">
                    <h2>Your Notes</h2>
                </div>
                <div className="innerCont">
                    <div id="note_container">
                        {items.map((itemval) => {
                            return (
                                <>
                                <div className='card'>
                                    <div className='card_body'>
                                        <h5 className='card_title'>{itemval.title} </h5>
                                        <p className='card_text'>{itemval.desp}</p>
                                        <div className='btns'>
                                            <button>Delete Note</button>
                                            <button>Edit Note</button>
                                        </div>
                                    </div>
                                </div>
                                </>
                            )
                        })}  
                    </div>
                </div>
            </div>
        </>
    );
}

export default Notes;
