import React, { useEffect, useState } from 'react';
import './note.css';
const Notes = () => {

    const [items, setItems] = useState([]);

    useEffect(() => {
        const items = JSON.parse(localStorage.getItem('items'));
        if (items) {
         setItems(items);
        }
    }, []);

    useEffect(() => {
        localStorage.setItem('items',JSON.stringify(items));
    }, [items]);

    const [detail, setDetail] = useState({
        title: document.getElementById("title"),
        desp: document.getElementById("desp"),
        color: ""
    });

    const inputEvent = (event) => {
        const value = event.target.value;
        const name = event.target.name;
        setDetail((prev) => {
            return {
                ...prev,
                [name] : value
            }
        });
    }

    const noteColor = () =>{
        if(document.getElementById("title").value == "" && document.getElementById("description").value == ""){
            alert('empty fields');
            return;
        }
        else{
            document.getElementById('colorHighlight').style.display = "flex";
            return;
        }
    }
    const listOfNotes = () =>{
        if(document.getElementById("addNote").innerText === "Update Note"){
            let updateCard = document.querySelector('.card');
            let i = updateCard.getAttribute('index');
            setItems((prevItems) => {
                const updatedItems = [...prevItems];
                console.log(updatedItems);
                updatedItems[i] = { ...detail };
                return updatedItems;
            });
            document.getElementById("addNote").innerText = "Add Note";
        }
        else{
            setItems((old) => {
                return [...old,detail];
            })
        }
        setDetail("");
        document.getElementById("title").value = "";
        document.getElementById("description").value = "";
        document.getElementById('colorHighlight').style.display = "none";
    }

    const editNote = (i) => {
        let title = document.getElementById("title");
        let desp = document.getElementById("description");
        title.value = items[i].title;
        desp.value = items[i].desp;
        setDetail({title: items[i].title, desp: items[i].desp})
        document.getElementById("addNote").innerText = "Update Note";
        let editCard = document.querySelector('.card');
        editCard.setAttribute('index', i)
    }

    const deleteNote = (i) => {
        setItems((prevItems) => {
            const updatedItems = [...prevItems];
            updatedItems.splice(i, 1);
            return updatedItems;
        });
    }

    return (
        <>
            <div className='container'>
                <div className='middlecont' id='inputCont'>
                    <div className="innerCont">
                        <label className="formLabel" >Title</label>
                        <input type="text" name='title' className="formControl" id="title" onChange={inputEvent} value={detail.title}/>
                    </div>
                    <div className="innerCont" >
                        <label className="formLabel" >Description</label>
                        <textarea className="formControl" name='desp' id="description" rows={5} onChange={inputEvent} value={detail.desp}/>
                    </div>
                    <div className="innerCont" id='btn_highlight'>
                        <div className='innerCont_btn'>
                            <button type="button" id="addNote" onClick={noteColor} ><i class="fa-solid fa-circle-plus"></i></button>
                        </div>
                        <div className='colorCode' id='colorHighlight'>
                            <i class="fa-solid fa-circle" onChange={inputEvent} onClick={listOfNotes} name="color" value="#d0e17a" style={{color: "#d0e17a"}}></i>
                            <i class="fa-solid fa-circle" onClick={()=>{inputEvent();listOfNotes();}} name="color" value="#ff9d48" style={{color: "#ff9d48"}}></i>
                            <i class="fa-solid fa-circle" onClick={()=>{inputEvent();listOfNotes();}} name="color" value="#67c6c0" style={{color: "#67c6c0"}}></i>
                            <i class="fa-solid fa-circle" onClick={()=>{inputEvent();listOfNotes();}} name="color" value="rgb(243 235 135)" style={{color: "rgb(243 235 135)"}}></i>
                            <i class="fa-solid fa-circle" onClick={()=>{inputEvent();listOfNotes();}} name="color" value="#ea94bb" style={{color: "#ea94bb"}}></i>
                        </div>
                    </div>
                </div>
                <div className='middlecont' id='noteCont'>
                    <div className="innerCont">
                        <h2>Notes</h2>
                    </div>
                    <div className="innerCont">
                        <div id="note_container">
                            {items.map((itemval, index) => {
                                return (
                                    <>
                                    <div className='card' id={index} style={{background: itemval.color}}>
                                        <div className='card_body'>
                                            <h5 className='card_title'>{itemval.title} </h5>
                                            <p className='card_text'>{itemval.desp}</p>
                                            <div className='btns'>
                                                <button onClick={() => deleteNote(index)}><i class="fa-solid fa-trash-can"></i></button>
                                                <button onClick={() => editNote(index)}><i class="fa-sharp fa-solid fa-pen"></i></button>
                                            </div>
                                        </div>
                                    </div>
                                    </>
                                )
                            })}  
                        </div>
                    </div>
                </div> 
            </div>
        </>
    );
}

export default Notes;
